#include <iostream.h>class tHenkilo {    char sukunimi[40];    char etunimi[20];    int syntymavuosi;  public :    tHenkilo( char *etunim, char *sukun , int syntvuosi );    void kysy();    void tulosta();};tHenkilo :: tHenkilo( char *etunim, char *sukun , int syntvuosi ){  strcpy( sukunimi, sukun );  strcpy( etunimi, etunim );  syntymavuosi = syntvuosi;}void tHenkilo :: kysy(){  cout << "Anna etunimi>";  cin >> etunimi;  cout << "Anna sukunimi>";  cin >> sukunimi;  cout << "Anna syntymavuosi>";  cin >> syntymavuosi;}void tHenkilo :: tulosta(){  cout << "Henkilon etunimi on " << etunimi << endl;  cout << " ja sukunimi " << sukunimi << endl;  cout << " ja syntymavuosi " << syntymavuosi << endl;}int main(){  tHenkilo henkilo("","",0);  henkilo.kysy();  henkilo.tulosta();  return 0;}