#include<stdlib.h>#include<string.h>class TVali {    int alku,loppu;  public :    TVali( int ialku, int iloppu);    void lue();    int Osuuko( TVali : Vali );}TVali :: TVali( int ialku, int iloppu ){  alku = ialku;loppu = iloppu;}int seurluku( char *mj, int ensim , int &i , int miinus )// Ohjelma etsii merkkijonosta mj positiivisen kokonaisluvun ja palauttaa sen.// Jos merkkijonosta ei läydy lukua, annetaan tuloksena -1.// Parametri ensim kertoo, onko kysymyksess³ v³lin ensimm³inen luku, jolloin// se voi p³³tty³ - merkkiin.// Luku kootaan merkkijonoon osamj, josta se muunnetaan tulokseen.// i on indeksi, josta etsint³ aloitetaan. Ohjelma palauttaa t³ss³// muuttajassa indeksin, jossa luku p³³ttyy, ts. - merkin j³lkeisen// merkin indeksin tai \0 -merkin indeksin.// Parametri miinus antaa arvokseen 0, jos luku ei p³³ty - merkkiin,// muutoin 1:n.{  char osamj[10];  int loppu,luku,tulos;  loppu=0;luku=0;miinus=0;  while (!loppu) {    if ((mj[i]>='0') && (mj[i]<='9')) luku=1;    if (ensim && (mj[i]=='-')) { loppu=1;i--;miinus=1;      // Ei lueta - merkki³ kokonaisluvuksi muunnettavaan merkkijonoon.    }    if (mj[i]=='\0') loppu=1;    osamj[i]=mj[i];i++;  }  osamj[i] = '\0';  tulos = atoi(osamj);  if (!luku) tulos = -1;  return tulos;}void TVali :: lue(){  char mj[10];  int i,miinus;  cout << "Anna v³li (0-5) >";  cin >> mj;  i=1;  if (strcmp(mj,'')==0) { alku=0;loppu=5; } else {    alku  = seurluku( mj, 1, i , miinus );    if (alku ==-1) alku=0;    if (!miinus) loppu=alku; else {      loppu = seurluku( mj, 0, i );      if (loppu==-1) loppu=5;    }  }}int TVali :: osuuko( Vali : TVali ){  if ((Vali.alku>=alku) && (Vali.loppu<=loppu)) return 1; else return 0;}