#include <conio.h>#include <iostream.h>class cLinjaAuto {    int paikkoja;    int vapaita;  public :    cLinjaAuto(int ipaikkoja, int ivapaita );    void tulosta();    int lisaa( int lisays );    int vahenna( int vahennys );};cLinjaAuto :: cLinjaAuto( int ipaikkoja, int ivapaita ){  paikkoja = ipaikkoja;  vapaita = ivapaita;}void cLinjaAuto :: tulosta(){  cout << "Linja-autossa on paikkoja " << paikkoja << " ja niista on vapaita "  << vapaita << endl;}int cLinjaAuto :: lisaa( int lisays ){  if (vapaita>=lisays) { vapaita-=lisays;return 1; } else return 0;}int cLinjaAuto :: vahenna( int vahennys ){  if (vapaita+vahennys<=paikkoja) { vapaita+=vahennys;return 1; } else return 0;}int valikko(){  clrscr();  cout << "Anna toiminto:" << endl;  cout << "1. Lisaa matkustajia" << endl;  cout << "2. Poista matkustajia" << endl;  cout << "3. Tulosta tilanne" << endl;  cout << "4. Lopeta" << endl;  return getch();}void teetoiminto( int toiminto , cLinjaAuto &LinjaAuto ){  int maara;  switch (toiminto) {    case '1' : cout << "Montako matkustajaa lisataan>";          cin >> maara;          LinjaAuto.lisaa(maara);          break;    case '2' : cout << "Montako matkustajaa poistetaan>";          cin >> maara;          LinjaAuto.vahenna(maara);          break;    case '3' : LinjaAuto.tulosta(); getch(); break;    default : break;  }}int main(void){  cLinjaAuto LinjaAuto(50,50);  int toiminto;  do {    toiminto = valikko();    teetoiminto( toiminto , LinjaAuto );  } while (toiminto!='4');}