#include <conio.h>#include <iostream.h>class cLinjaAuto {    int paikkoja;    int vapaita;  public :    cLinjaAuto(int ipaikkoja );    void tulosta();    int lisaa( int lisays );    int vahenna( int vahennys );    int tilaa();};cLinjaAuto :: cLinjaAuto( int ipaikkoja ){  paikkoja = ipaikkoja;vapaita = ipaikkoja;}void cLinjaAuto :: tulosta(){  cout << "Linja-autossa on paikkoja " << paikkoja << " ja niista on vapaita "  << vapaita << endl;}int cLinjaAuto :: lisaa( int lisays ){  int yli;  if (vapaita>=lisays) { vapaita-=lisays;return 0; } else    { yli=lisays-vapaita;vapaita=0;return yli; }}int cLinjaAuto :: vahenna( int vahennys ){  if (vapaita+vahennys<=paikkoja) { vapaita+=vahennys;return 1; } else return 0;}int cLinjaAuto :: tilaa(){  return vapaita;}int main(void){  cLinjaAuto pikkubussi(10),isobussi(45);  pikkubussi.lisaa(4); pikkubussi.tulosta();  isobussi.lisaa(30);  isobussi.tulosta();  int yli = pikkubussi.lisaa(15);  isobussi.lisaa(yli);  pikkubussi.tulosta(); isobussi.tulosta();  if (isobussi.tilaa())    cout << "Isoon bussiin mahtuu!" << endl;  getch();  return 0;}