#include<iostream.h>#include<string>using namespace std;#include<string.h>int aVert( char *k , char *jono ){  int indeksi;  indeksi=strspn(jono,k);  if (indeksi==strlen(jono))    return 0;  else return 1;}int cVert( char *jono, char *k ){  int indeksi;  indeksi = strcspn(jono,k);  if (indeksi==strlen(jono))    return 0;  else return 1;}int main(){  // a. Onko merkkijonossa jono muita kirjaimia kuin k:n sis³lt³m³t  char jono[40]="kissa", k[5]="aik", *os;  int tulos;  tulos = aVert(k,jono);  cout << endl;  if (tulos) cout << "On." << endl; else cout << "Ei ole." << endl;  strcpy(k,"aiks");  tulos = aVert(k,jono);  if (tulos) cout << "On." << endl; else cout << "Ei ole." << endl;  // b. Miss³ on jonon viimeinen '\'  strcpy(jono,"C:\\OMAT\\OHJELMOI\\VESA\\KOE.C");  os = strrchr(jono,'\\');  cout << os << endl;  // c. Onko jonossa jokin kirjain joukosta k.  strcpy(jono,"kissa");  strcpy(k,"ibm");  tulos=cVert(jono,k);  if (tulos) cout << "On." << endl; else cout << "Ei ole." << endl;  strcpy(k,"pc");  tulos=cVert(jono,k);  if (tulos) cout << "On." << endl; else cout << "Ei ole." << endl;  return 0;}