#include<iostream.h>int suurin_kirjeen_paino(double rahaa){  if (rahaa<2.10) return 0;  if (rahaa<3.40) return 50;  if (rahaa<5.20) return 100;  if (rahaa<9.30) return 200; else return 500;}int main(){  int grammoja;  double rahaa;  cout << "Paljonko sinulla on postimerkkirahaa>";  cin >> rahaa;  grammoja = suurin_kirjeen_paino( rahaa );  cout << "Saat lŠhetettyŠ tŠllŠ rahalla korkeintaan " << grammoja <<  " grammaa painavan kirjeen. " << endl;  return 0;}