#include<iostream.h>int suurin_kirjeen_paino(double rahaa){  double rajat[4]={2.10,3.40,5.20,9.30};  int tulokset[5]={0,50,100,200,500},i;  for(i=0;i<=3;i++) if (rahaa<rajat[i]) return tulokset[i];  return tulokset[4];}int main(){  int grammoja;  double rahaa;  cout << "Paljonko sinulla on postimerkkirahaa>";  cin >> rahaa;  grammoja = suurin_kirjeen_paino( rahaa );  cout << "Saat lŠhetettyŠ tŠllŠ rahalla korkeintaan " << grammoja <<  " grammaa painavan kirjeen. " << endl;  return 0;}