#include<iostream.h>int palindromi( char *mj ){  int pituus,i;  pituus=strlen(mj);  for(i=0;i<pituus/2-1;i++)    if (mj[i]!=mj[pituus-1-i]) return 0;  return 1;}int main(){  char mj[40];  int tulos;  cout << "Anna vŠlimerkitšn merkkijono>";  cin >> mj;  tulos = palindromi( mj );  if (tulos) cout << "Merkkijonosi on palindromi." << endl; else    cout << "Merkkijonosi ei ole palindromi." << endl;}