#include<iostream.h>int poista(int taulukko[], int lkm, int n){  int i,j;  for(i=0;i<lkm;i++)    if (taulukko[i]==n) {      // Poistetaan taulukon i:s alkio ja v³hennet³³n lkm:³³.      for(j=i+1;j<lkm;j++) taulukko[j-1] = taulukko[j];      lkm--;    };  return lkm; // Palautetaan j³ljelle j³³nyt taulukon koko}void tulosta_taulukko( int taulukko[], int lkm ){  int i;  for(i=0;i<lkm;i++) cout << taulukko[i] << " ";  cout << endl;}int main(){  int lkm=6,taul[6]={4,7,6,3,6,2};  lkm = poista( taul, lkm, 6 );  tulosta_taulukko( taul, lkm );  return 0;}