#include<iostream.h>#include<iomanip.h>class cTaulukko {  int max_koko;  int lkm;  int *alkiot;public :  cTaulukko(int akoko) {    max_koko = 0;    alkiot = new int[akoko];    if (alkiot) max_koko = akoko;    lkm = 0;  }  ~cTaulukko() { if ( max_koko ) delete [] alkiot; max_koko=0; }  int lisaa(int luku) {    if ( lkm >= max_koko ) return 1;    alkiot[lkm] = luku;    lkm++;    return 0;  }  ostream &tulosta(ostream &os=cout) const;//  stream &operator<<(ostream &os,const cTaulukko &taulukko)//    { return taulukko.tulosta();  }  int alkio(int i) { if (i>=1 && i<lkm) return alkiot[i]; else return -1; }  int lukumaara() { return lkm; }  int sijoita(cTaulukko taulukko);  cTaulukko &operator=(cTaulukko taulukko)    { sijoita(taulukko); return *this; }  int poista(int i);};int cTaulukko::sijoita(cTaulukko taulukko){  int i,lkm2;  lkm2 = taulukko.lukumaara();  if ( lkm2 > max_koko) return 1;  for(i=0;i<lkm2;i++) alkiot[i]=taulukko.alkio(i);  return 0;}int cTaulukko::poista(int i){  int j;  if (i>=1 && i<=lkm) {    for(j=i;j<lkm;j++) alkiot[j-1] = alkiot[j];    lkm--;    return 0;  } else return 1;}ostream& cTaulukko::tulosta(ostream &os) const{  int i;  for(i=0;i<lkm;i++)    os << setw(5) << alkiot[i];  os << endl;  return os;}int main(void){  cTaulukko luvut(7);  luvut.lisaa(0); luvut.lisaa(2); // Ilo on t³³ll³;  luvut.tulosta();cout << endl;//  cout << luvut << endl;  cTaulukko taul(8);  taul.sijoita(luvut);  // taul = luvut;  taul.tulosta();  return 0;}