#include<iostream.h>class cPerusluokka {public :  cPerusluokka()  { cout << "Perusluokan konstruktori" << endl; };  virtual ~cPerusluokka() { cout << "Perusluokan destruktori" << endl; };};class cPeritty1 : public cPerusluokka{public :  cPeritty1() { cout << "cPeritty1 -luokan konstruktori" << endl; }  ~cPeritty1() { cout << "cPeritty1 -luokan destruktori" << endl; }};class cPeritty2 : public cPerusluokka{public :  cPeritty2() { cout << "cPeritty2 -luokan konstruktori" << endl; }  ~cPeritty2() { cout << "cPeritty2 -luokan destruktori" << endl; }};int main(){  cPerusluokka *oliot[5]={ NULL };  cout << endl;  oliot[0] = new cPerusluokka;  oliot[1] = new cPeritty1;  oliot[2] = new cPeritty2;  delete oliot[2];  delete oliot[1];  delete oliot[0];  return 0;}