#include<conio.h>#include<stdio.h>#include<string.h>#include<iostream.h>#include "c:\oma\apuohj.h"#include "c:\oma\jaejakso.h"int kysy_jaejakso(cJaejakso &jaejakso){  char rivi[80],*p;  int loppuparammaara=0;  int alkukirja,alkuluku,alkujae;  int loppuparam1,loppuparam2,loppuparam3;  char loppukirja,loppuluku,loppujae;  while(getch()!=32);  cout << "Anna jaejakso:" << endl;  strcpy(rivi,"");  lue_jono(rivi,80);  p=strtok(rivi,":");if (!p) return 0;  if (sscanf(p,"%d",&alkukirja)!=1) return 0;  p=strtok(NULL,":");if (!p) return 0;  if (sscanf(p,"%d",&alkuluku)!=1) return 0;  p=strtok(NULL,"-");if (!p) return 0;  if (sscanf(p,"%d",&alkujae)!=1) return 0;  p=strtok(NULL,":"); if (!p) return 0;  if (sscanf(p,"%d",&loppuparam1)!=1) return 0;  loppuparammaara++;  p=strtok(NULL,":");  if (p) {    loppuparammaara++;    if (sscanf(p,"%d",&loppuparam2)!=1) return 0;    p=strtok(NULL,":");    if (p) {      if (sscanf(p,"%d",&loppuparam3)!=1) return 0;      loppuparammaara++;    }  }  switch (loppuparammaara) {    case 1 : loppukirja=alkukirja;loppuluku=alkuluku;	     loppujae = loppuparam1; break;    case 2 : loppukirja=alkukirja;	     loppuluku=loppuparam1;loppujae=loppuparam2; break;    case 3 : loppukirja=loppuparam1;loppuluku=loppuparam2;	     loppujae=loppuparam3; break;  }  jaejakso.aseta_alkujae( alkukirja, alkuluku, alkujae );  jaejakso.aseta_loppujae( loppukirja, loppuluku, loppujae );  return 1;}void tulosta_jaejakso( cJaejakso &jaejakso ){  char alkukirja,alkuluku,alkujae;  char loppukirja,loppuluku,loppujae;  jaejakso.anna_alkujae( &alkukirja, &alkuluku, &alkujae );  jaejakso.anna_loppujae( &loppukirja, &loppuluku, &loppujae );  cout << endl;  cout << "Jaejakso on: "; printf("%d", alkukirja ); cout << ":";  if (alkukirja==loppukirja) {    printf("%d",alkuluku); cout << ":";    if (alkuluku==loppuluku) {      printf("%d",alkujae); cout << "-";      printf("%d",loppujae); cout << endl;    } else {      printf("%d", alkujae ); cout << "-";      printf("%d", loppuluku ); cout << ":";      printf("%d", loppujae );    }  } else {    printf("%d", alkuluku ); cout << ":";    printf("%d", alkujae ); cout << "-";    printf("%d", loppukirja ); cout << ":";    printf("%d", loppuluku ); cout << ":";    printf("%d", loppujae ); cout << endl;  }}int main(void){  cJaejakso jaejakso;  if (kysy_jaejakso( jaejakso )) tulosta_jaejakso( jaejakso ); else    cout << "Syättä ei onnistunut!" << endl;  return 0;}